const modalElement=document.querySelector("div.modal"),modalImageElement=document.querySelector(".modal .modal__img"),burguerMenuElement=document.querySelector(".header__burguer-menu");async function handleFormOnSubmit(){const e=await getFormData();0!==e.grecaptcha.length?(sendEmail("https://leandrofreelancer.herokuapp.com/api/v1/email",e),clearFormInputs()):alert("Por favor, verifique se você não é um robô.")}async function getFormData(){return{name:document.querySelector("#contact #name").value,email:document.querySelector("#contact #email").value,message:document.querySelector("#contact #message").value,grecaptcha:await grecaptcha.getResponse()}}function sendEmail(e,t){fetch(e,{headers:{"Content-Type":"application/json"},method:"POST",body:JSON.stringify(t)}).then((()=>{console.log("Email enviado")})).catch((()=>{console.error("Não foi possível enviar o email")}))}function clearFormInputs(){document.querySelector("#contact #name").value="",document.querySelector("#contact #email").value="",document.querySelector("#contact #message").value=""}function toggleModal(e="",t=""){modalImageElement.src=e,modalImageElement.alt=t,modalElement.classList.toggle("modal--hidden")}function toggleBurguerMenu(){burguerMenuElement.classList.toggle("active")}const swiper=new Swiper(".swiper",{direction:"horizontal",loop:!0,slidesPerView:window.outerWidth>767?5:1,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});document.querySelectorAll(".portfolio__img").forEach((e=>{e.addEventListener("click",(()=>{toggleModal(e.src,e.alt)}))}));